import React, { useState } from "react";

const PostList = () => {
  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "Atomic Habits",
      content: "An easy & proven way to build good habits and break bad ones.",
      author: "James Clear",
    },
    {
      id: 2,
      title: "The Alchemist",
      content: "A journey of a shepherd boy following his dream.",
      author: "Paulo Coelho",
    },
    {
      id: 3,
      title: "Ikigai",
      content: "The Japanese secret to a long and happy life.",
      author: "Héctor García",
    },
    {
      id: 4,
      title: "Deep Work",
      content: "Rules for focused success in a distracted world.",
      author: "Cal Newport",
    },
    {
      id: 5,
      title: "Rich Dad Poor Dad",
      content: "What the rich teach their kids about money.",
      author: "Robert Kiyosaki",
    },
  ]);

  const [newPost, setNewPost] = useState({
    title: "",
    content: "",
    author: "",
  });

  const handleChange = (e) => {
    setNewPost({ ...newPost, [e.target.name]: e.target.value });
  };

  const handleAddPost = (e) => {
    e.preventDefault();
    const newId = posts.length + 1;
    setPosts([...posts, { id: newId, ...newPost }]);
    setNewPost({ title: "", content: "", author: "" });
  };

  const handleDelete = (id) => {
    const updatedPosts = posts.filter((post) => post.id !== id);
    setPosts(updatedPosts);
  };

  return (
    <div>
      <h2>Add New Post</h2>
      <form onSubmit={handleAddPost} style={{ marginBottom: "30px" }}>
        <input
          name="title"
          placeholder="Title"
          value={newPost.title}
          onChange={handleChange}
          required
          style={{ marginRight: 10 }}
        />
        <input
          name="content"
          placeholder="Content"
          value={newPost.content}
          onChange={handleChange}
          required
          style={{ marginRight: 10 }}
        />
        <input
          name="author"
          placeholder="Author"
          value={newPost.author}
          onChange={handleChange}
          required
        />
        <button type="submit" style={{ marginLeft: 10 }}>Add</button>
      </form>

      <h2>All Blog Posts</h2>
      {posts.map((post) => (
        <div
          key={post.id}
          style={{
            border: "1px solid #ccc",
            padding: 15,
            margin: 10,
            borderRadius: 8,
          }}
        >
          <h3>{post.title}</h3>
          <p>{post.content}</p>
          <p>
            <em>— {post.author}</em>
          </p>
          <button onClick={() => handleDelete(post.id)} style={{ background: "red", color: "white", border: "none", padding: "5px 10px", cursor: "pointer", borderRadius: 4 }}>
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default PostList;
