// components/PostList.jsx
import React, { useState } from "react";

const PostList = () => {
  // Static 5 blog posts (books)
  const [posts] = useState([
    {
      id: 1,
      title: "Atomic Habits",
      content: "An easy & proven way to build good habits and break bad ones.",
      author: "James Clear",
    },
    {
      id: 2,
      title: "The Alchemist",
      content: "A journey of a shepherd boy following his dream.",
      author: "Paulo Coelho",
    },
    {
      id: 3,
      title: "Ikigai",
      content: "The Japanese secret to a long and happy life.",
      author: "Héctor García",
    },
    {
      id: 4,
      title: "Deep Work",
      content: "Rules for focused success in a distracted world.",
      author: "Cal Newport",
    },
    {
      id: 5,
      title: "Rich Dad Poor Dad",
      content: "What the rich teach their kids about money.",
      author: "Robert Kiyosaki",
    },
  ]);

  return (
    <div>
      {posts.map((post) => (
        <div key={post.id} style={{ border: "1px solid #ccc", padding: 15, margin: 10 }}>
          <h3>{post.title}</h3>
          <p>{post.content}</p>
          <p>
            <em>— {post.author}</em>
          </p>
        </div>
      ))}
    </div>
  );
};

export default PostList;
